INSERT INTO `t_new_house` VALUES (1000690, 57909, '3房', 'SOLD_OUT', 'HARDCOVER', '生态宜居', NULL, '<p>\n	教育：维多利亚幼儿园、晶晶幼儿园、童歌博尔幼儿园、景秀中学、狮岭小学、景龙小学、景龙小学学校、广州大学建筑设计研究院深圳分院</p>\n<p>\n	商业：香梅市场、岁宝百货、岁宝明星店、民润等</p>\n<p>\n	医疗：深圳景田医院、深圳市北大医院</p>\n<p>\n	邮局：景田邮局、香蜜湖邮局</p>\n<p>\n	银行：华夏银行、建设银行、中国银行、交通银行、招商银行、民生银行、平安银行等</p>\n<p>\n	其他：莲花山公园、笔架山公园&nbsp;</p>', '<p>\n	地铁：距地铁2号线莲花西站仅500米、景田站600米、1号线香蜜湖站800米</p>\n<p>\n	公交：鲁班大厦，市政大厦 公交线路：104路、107路、108路、11路、14路、15路、209路、213路、215路、21路、M363路、M383路、28路等线路均可到达。</p>', NULL, '2014-5-7 16:51:17', '2016-6-27 19:29:20', 0, 304, 849, 201, -0.32300000000000, 'ZHUAQU', 12, 3, 5, 30000, NULL, NULL, '2014-4-19', '2065', 'ENABLED', NULL, NULL, NULL, 0, 'http://img4.qfang.com/shenzhen/community/jingtian/lubandasha/os/{size}/baeb2081-c603-4f9e-acec-adfdd5c2edd5.jpg', 'NORMAL', NULL, NULL);
