INSERT INTO `t_new_house` VALUES (2001737, 2303460, '113-207平', 'FOR_SALE', 'NAKE_DECORATED', 'UNLIMITED_PURCHASE', NULL, '<p>\n	教育：实验中学、一中、十一小、南山中学、惠州学院等国际双语幼儿园 、14000平米小学</p>\n<p>\n	商业：沃尔玛、天虹、海雅、人人乐、建材装潢商场、三环装饰城、万饰城</p>\n<p>\n	银行：五大商业银行</p>\n<p>\n	医疗：协和医院、中心医院、华康医院</p>\n<p>\n	其他：南山森林公园、高榜山、红花湖、西湖、18000平米人工湖、400亩郊野公园、天际泳池、奢华皇室会所</p>', '<p>\n	自驾线路：</p>\n<p>\n	深圳方向</p>\n<p>\n	梅林关→梅观高速→机荷高速→惠盐高速→白云前收费站出; 自驾车线路二：罗湖→沿海高速→盐坝高速→惠盐高速→白云前收费站出</p>', NULL, '2013-11-5 16:04:36', '2016-3-29 23:59:59', 0, 126, 155, 44, 0.00700000000000, 'ZHUAQU', 49, 71, 36, 6300, NULL, NULL, '2013-9-20', '1001', 'DELETED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/longfeng/jiayiyuan/os/{size}/57400c9b-51e9-4535-8e41-f9a930618fd2.jpg', 'NORMAL', NULL, NULL);
INSERT INTO `t_new_house` VALUES (2001738, 2303461, '', 'IN_THE_SALE', 'NAKE_DECORATED', '豪华居住区,水景地产,宜居生态地产', '', '<p>\n	教育：东江小学、河南岸中心小学、惠州一中、惠州市九中、河南岸中学、东江小学、 河南岸中心小学、绿杨小学、华罗庚中学、北师大附中和惠州大学等教育配套。</p>\n<p>\n	商业：沃尔玛、天虹、人人乐、海雅、吉之岛等大型商超</p>\n<p>\n	医疗：惠州市第三人民医院、河南岸医院、惠州市第四人民医院等医疗配套。</p>\n<p>\n	邮局：中国邮政</p>\n<p>\n	银行：建设银行、工商银行、广发银行、中国银行等</p>\n<p>\n	其他：金山湖公园</p>\n<p>\n	珑湖湾东岸规划有12班的公立幼儿园，幼儿园建筑面积3900多平米；另外还拥有3000平米阳光草坪，1000平米人工湖；整个园林打造成三时有花、四季有景的花海园林；</p>', '<p>\n	自驾：珑湖湾东岸东面和北面为24m宽的金山大桥市政规划道路，南面和西面为10m宽的三环南路市政规划道路，珑湖湾东岸位于金山湖豪宅片区的核心区域，北向10分钟畅达东平、江北，西向5分钟直达演达、麦地核心商业圈，南向直通惠阳、大亚湾。</p>\n<p>\n	珑湖湾东岸暂时没有公交车到达项目。</p>', NULL, '2013-11-5 16:07:30', '2016-3-29 23:59:59', 0, 151, 300, 83, -0.09100000000000, 'ZHUAQU', 6, 35, 17, 6500, '广东世邦物业', '惠州腾凯实业有限公司', '2013-8-24', '1001', 'DELETED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/shangdongping/longhuwandongan/os/{size}/e138f6c9-823c-4a22-bb08-7408130390f7.jpg', 'NORMAL', NULL, NULL);
