INSERT INTO `t_new_house` VALUES (2001821, 1106897, NULL, 'FOR_SALE', 'NAKE_DECORATED', 'UNLIMITED_PURCHASE', NULL, '<p>\n	教育：古塘坳幼儿园、古塘坳小学、惠州师范高级中学、实验小学、惠州地质中学；</p>\n<p>\n	医疗：703医院、173医院、武警医院；</p>\n<p>\n	商业：有南线客运站综合体商业、金迪星苑成熟商业氛围</p>\n<p>\n	其他：佳兆业1号规划有自身商业</p>', '<p>\n	公交：5、6、11、25、26、27、203、206、208、209等可达项目。</p>\n<p>\n	其他：“1+1+1+4”立体式交通，即一个客运交通枢纽、一条轨道交通、一个城市候机楼、4条城市主干道、多条公交线路。一个交通枢纽指毗邻惠州未来第一客运交通枢纽——客运南站（也称“奔腾的火车”）；一条轨道交通指临近2号线莞惠轻轨“客运南站”站（该站为惠城首站）；一个城市候机楼为毗邻深圳机场惠州候机楼；四条城市主干道为鹅岭南路、仲恺大道、金榜路、三环南路，一站式通达全城。</p>', NULL, '2014-3-12 10:41:57', '2016-3-4 18:00:00', 0, 48, 34, 14, -0.17800000000000, 'ZHUAQU', NULL, 3, 7, 0, NULL, NULL, NULL, '1001', 'ENABLED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/longfeng/jiazhaoye1hao/os/{size}/357d72cb-c9a3-4d6d-b8d7-42ef0fcf2922.jpg', 'NORMAL', NULL, NULL);
