INSERT INTO `t_new_house` VALUES (3002620, 3503327, '345-681平', 'IN_THE_SALE', 'NAKE_DECORATED', '投资地产', '<br />', '<p>\n	教育：国际双语普林斯顿幼儿园、南方外国语学校、东莞中学松山湖学校、松山湖中心小学</p>\n<p>\n	商业：凯悦酒店、喜悦酒店、万科生活广场、长城世家商业街</p>\n<p>\n	医疗：广东医学院附属医院、松山湖北部医院</p>\n<p>\n	银行：中国银行、东莞银行、建设银行、广发银行、中国邮政</p>\n<p>\n	其他：沟谷公园、松湖烟雨、状元笔公园、中心公园、火烧山公园 内部配套：万科•生活广场、会所</p>', '<p>\n	公交：松山湖站</p>', NULL, '2013-8-8 16:10:07', '2016-3-29 23:59:59', 0, 1097, 84, 16, -0.15000000000000, 'ZHUAQU', 5, 55, 23, 10000, '东莞市万科物业服务有限公司', '东莞市盛宏实业投资有限公司 ', '2012-8-18', '401', 'DELETED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/nancheng/wankesonghuzhongxin/os/{size}/31679622-1fc8-4299-b556-8e28e4cffea3.jpg', 'NORMAL', NULL, NULL);
INSERT INTO `t_new_house` VALUES (3002630, 3504325, '160-180平', 'IN_THE_SALE', 'NAKE_DECORATED', '地铁沿线,花园洋房,教育地产', '<br />', '<p>\n	教育：市高级中学、宏远外国语学校、南开学校、3公里以内有阳光小学、阳光二小、阳光三小、胜和小学等、新基幼儿园</p>\n<p>\n	商业：海雅百货、沃尔玛(时尚岛)、家乐福、时尚岛、时尚电器、国美电器、南城步行街、城市风景商业街</p>\n<p>\n	银行：建行、工行、兴业银行、中国银行、恒生银行、东莞银行等</p>\n<p>\n	医疗：3公里以内有南城医院、玛利亚妇产医院、康华医院等</p>\n<p>\n	其他：天利中央花园拥有20000平米水景园林，3500平米度假式人造沙滩泳池</p>', '<p>\n	公交：国际公馆站、西平路口站</p>', NULL, '2013-8-23 15:30:00', '2016-3-4 18:00:00', 0, 1460, 515, 73, -0.24700000000000, 'ZHUAQU', 17, 119, 88, 12000, '深圳市天利物业管理有限公司东莞分公司', '东莞市天利房地产开发有限公司', '2011-7-23', '2304', 'ENABLED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/dongguan/tianlizhongyanghuayuan/os/{size}/3790c0c6-ea76-4c91-86d2-1b10308d286e.jpg', 'NORMAL', NULL, NULL);
