INSERT INTO `t_new_house` VALUES (3002732, 3503480, '168平4房', 'IN_THE_SALE', 'NAKE_DECORATED', 'LIVABLE,WATERSCAPE', '<br />', '<p>\n	教育：白濠小学、白濠幼儿园</p>\n<p>\n	商业：天亿购物广场、丰濠购物广场、白濠综合市场</p>\n<p>\n	医疗：爱华综合门诊部、厚街医院白濠门诊部、白濠沿河路</p>\n<p>\n	邮局：中国邮政</p>\n<p>\n	银行：中国邮政、工行、建行、农行、东莞农村商业银行等</p>\n<p>\n	其他：3300㎡娱乐主题会所、50000㎡健身运动泛会所，3000㎡沙滩泳池</p>', '<p>\n	广深高铁东莞唯一站点白沙站、东莞城市地铁R2线、穗莞深城际轻轨、广深高速、虎岗高速、广深沿江高速、莞太路、博览大道等。</p>', NULL, '2013-10-28 17:24:29', '2016-3-29 23:59:59', 0, 70, 117, 41, -0.12600000000000, 'LIANDONG', 2, 41, 19, 8000, NULL, NULL, '2013-8-18', '2307', 'DELETED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/houjie/yushuinanan/os/{size}/ac62d95a-153a-4980-b111-d1afdb0a987c.jpg', 'NORMAL', NULL, NULL);
