INSERT INTO `t_new_house` VALUES (3002748, 3503496, '', 'IN_THE_SALE', 'NAKE_DECORATED', '豪华居住区,特色别墅', '<br />', '<p>\n	社区配置休闲会所，会所享有游泳池及其它的人造景观资源 1000米长社区体育公园、波西塔诺风情会馆、地中海风情商业街、风情漫步大道 波西塔诺风情会所及泳池，风情商业街，2万平米体育公园，幼儿园，以及健身房、健康中心、篮球场等，社区生活休闲娱乐配体十分齐全。</p>', '<p>\n	自驾：誉景名居位于常平镇环城路边上，到常虎高速常平出口仅7分钟车程、通过常虎高速一个小时就可以到达深圳、广州等地机场，半个小时可到达虎门；距离莞樟路不到5分钟车程，可以迅速到达东莞各个镇区（前往大朗镇只需5分钟的车程，至黄江镇只需不到10分钟的车程），交通便捷，升值前景广阔。</p>\n<p>\n	公交站点：苏坑站</p>\n<p>\n	附近公交：常平8路</p>', NULL, '2013-10-29 10:25:29', '2016-3-29 23:59:59', 0, 129, 148, 44, -0.09800000000000, 'LIANDONG', 6, 37, 93, 9500, '东莞市建和物业管理有限公司', '东莞市金色家园开发建造有限公司', '2012-11-26', '2306', 'DELETED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/changping/yujingmingju/os/{size}/82c23d81-951a-40f7-bfe8-5d83ee8d007e.jpg', 'NORMAL', NULL, NULL);
