INSERT INTO `t_new_house` VALUES (3002801, 3505735, NULL, 'IN_THE_SALE', 'NAKE_DECORATED', 'ALONG_LINE', '<div class=\"box mt15 budSelPonint\" style=\"margin: 15px 0px 0px; padding: 0px; border: 1px solid rgb(224, 224, 224); outline: 0px; font-style: inherit; font-family: inherit; box-sizing: border-box;\">\n	<div class=\"box_bd budCont\" style=\"margin: 0px; padding: 0px 10px 10px; border: 0px; outline: 0px; font-style: inherit; font-size: 14px; font-family: inherit; line-height: 28px;\">\n		<b style=\"color: rgb(51, 51, 51); font-family: tahoma, Arial, 宋体; font-size: 12px; line-height: 18px; background-color: rgb(255, 255, 255);\"><b>1号商业<br />\n		3.8万㎡ 吉之岛强势进驻，超3000㎡国际影院<br />\n		<br />\n		2个中心&nbsp;<br />\n		大朗常平 寮步 东坑 黄江 5镇交汇中心<br />\n		大朗新行政中心<br />\n		双中心区位 释放40万中高端商机<br />\n		<br />\n		3轨聚首<br />\n		<br />\n		莞惠城际轻轨&nbsp;<br />\n		R1线 松山湖站<br />\n		R3线 松山湖北站<br />\n		<br />\n		4大街区<br />\n		国际购物中心&nbsp;<br />\n		缤纷生活街<br />\n		品牌名店街<br />\n		游逛式 情景商业街</b></b></div>\n</div>', '<p>\n	教育：宏阳小学、北平学校、旭升学校、大朗中学、宏阳幼儿园、聖堂幼儿园</p>\n<p>\n	商业：雄发百货、源丰商场、柏惠百货、红荔超市、家家福超市、业丰酒店、时代酒店、富华酒店、湘阁里辣湘菜馆、业丰中餐厅、食为天、江南人家、潮粤饭店等</p>\n<p>\n	医疗：大朗医院、大朗新区医疗</p>\n<p>\n	邮局：东莞市邮政局大朗分局</p>\n<p>\n	银行：中国银行、工商银行、邮政储蓄、大朗农村信用合作社、东莞农村商业银行大朗分行</p>', '<p>\n	远大城市广场位于常平、寮步、东坑、黄江、大朗交汇处，毗邻松山湖，经莞樟路、朗东路及朗常路与5大镇区中心区车程10分钟以内，莞惠城际线大朗站地铁口与远大城市广场地下地上双核商业无缝对接，同时毗邻轻轨R1线和R3线，融入20分钟城区生活圈内，至东莞火车站及火车东站只有5分钟车程，30分钟可畅通各经济重镇，于东莞、惠州、深圳搭建多维交通路网，快速融入珠三角生活圈。</p>', NULL, '2013-10-30 15:08:08', '2016-3-4 18:00:00', 0, 21, 21, 3, -0.58600000000000, 'LIANDONG', 3, 9, 15, 7900, NULL, NULL, '2013-10-30', '2307', 'ENABLED', NULL, NULL, NULL, 0, 'http://img.qfang.com/community/shijie/yuandachengshiguangchang/os/{size}/baba92d3-ae6e-4b74-9df8-0e3491ec501d.jpg', 'NORMAL', NULL, NULL);
